import "./App.css";

function App() {
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [loading, setLoading] = useState(false);

  const handleAsk = async (question) => {
    const response = await fetch("http://localhost:8000/query", {  // âœ… Correct
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ question }),
    });

  };


  return (
    <div className="app">
      <h1>ðŸ“˜ Math Routing Agent</h1>
      <QuestionForm onAsk={handleAsk} />
      {loading ? <p>Thinking...</p> : <AnswerDisplay answer={answer} />}
      {answer && <FeedbackForm question={question} />}
    </div>
  );
}

export default App;
